import { ImageBackground, Pressable, StyleSheet, View } from "react-native";
import { MyText } from "./MyText";
import { StackNavigationProp } from "@react-navigation/stack";
import { RootStackParamList } from "../App";
import { useNavigation } from "@react-navigation/native";
import { Login } from "./Login";

type loginScreenProp = StackNavigationProp<RootStackParamList, "Login">;

export function Splash() {
  const navigation = useNavigation<loginScreenProp>();
  const { startTransition } = useBetweenPages(<Login />);

  const useAnimation = () => {
    navigation.navigate("Login");
  };

  return (
    <View style={styles.container}>
      <ImageBackground
        source={require("../assets/static-images/splash.webp")}
        resizeMode="cover"
        style={styles.image}
      >
        <MyText styles={styles.text} text="Tetris" />
        <Pressable
          style={styles.button}
          android_ripple={{ color: "blue", radius: 5 }}
          onPress={() => useAnimation()}
        >
          <MyText styles={styles.text} text="Play Game" />
        </Pressable>
      </ImageBackground>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  image: {
    flex: 1,
    justifyContent: "center",
  },
  text: {
    color: "white",
    fontSize: 42,
    lineHeight: 84,
    textAlign: "center",
    backgroundColor: "#000000c0",
  },
  button: {
    alignItems: "center",
    justifyContent: "center",
    paddingVertical: 12,
    paddingHorizontal: 32,
    borderRadius: 4,
    elevation: 3,
    backgroundColor: "black",
  },
});
